(6, 6, '_wp_attachment_metadata', 'a:5:{s:5:\"width\";i:2878;s:6:\"height\";i:1480;s:4:\"file\";s:23:\"2020/10/maintenance.png\";s:5:\"sizes\";a:6:{s:6:\"medium\";a:4:{s:4:\"file\";s:23:\"maintenance-300x154.png\";s:5:\"width\";i:300;s:6:\"height\";i:154;s:9:\"mime-type\";s:9:\"image/png\";}s:5:\"large\";a:4:{s:4:\"file\";s:24:\"maintenance-1024x527.png\";s:5:\"width\";i:1024;s:6:\"height\";i:527;s:9:\"mime-type\";s:9:\"image/png\";}s:9:\"thumbnail\";a:4:{s:4:\"file\";s:23:\"maintenance-150x150.png\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:9:\"image/png\";}s:12:\"medium_large\";a:4:{s:4:\"file\";s:23:\"maintenance-768x395.png\";s:5:\"width\";i:768;s:6:\"height\";i:395;s:9:\"mime-type\";s:9:\"image/png\";}s:9:\"1536x1536\";a:4:{s:4:\"file\";s:24:\"maintenance-1536x790.png\";s:5:\"width\";i:1536;s:6:\"height\";i:790;s:9:\"mime-type\";s:9:\"image/png\";}s:9:\"2048x2048\";a:4:{s:4:\"file\";s:25:\"maintenance-2048x1053.png\";s:5:\"width\";i:2048;s:6:\"height\";i:1053;s:9:\"mime-type\";s:9:\"image/png\";}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:13:\"shutter_speed\";s:1:\"0\";s:5:\"title\";s:0:\"\";s:11:\"orientation\";s:1:\"0\";s:8:\"keywords\";a:0:{}}}'),
(19, 9, '_form', '<div class=\"row\">\n  <div class=\"col-md-6\" data-children-count=\"1\">\n    <label for=\"firstname\">Prenom<span class=\"blue\" data-children-count=\"0\">  *</span></label>\n    [text* firstname class:mb-2]\n  </div>\n  <div class=\"col-md-6\" data-children-count=\"1\">\n    <label for=\"name\">Nom<span class=\"blue\" data-children-count=\"0\"> *</span></label>\n    [text* lastname class:mb-2]\n  </div>\n  <div class=\"col-md-6\" data-children-count=\"1\">\n    <label for=\"email\">Email<span class=\"blue\" data-children-count=\"0\"> *</span></label>\n    [email* your-email class:mb-2]\n  </div>\n  <div class=\"col-md-6\" data-children-count=\"1\">\n    <label for=\"phone\">Téléphone</label>\n    [tel your-phone class:mb-2]\n  </div>\n  <div class=\"col-md-12\" data-children-count=\"1\">\n    <label for=\"name\">Objet<span class=\"blue\" data-children-count=\"0\"> *</span></label>\n    [text* your-subject class:mb-2]\n  </div>\n  <div class=\"col-md-12\" data-children-count=\"1\">\n    <label for=\"message\">Message<span class=\"blue\" data-children-count=\"0\"> *</span></label>\n    [textarea* your-message class:mb-2]\n  </div>\n  <div class=\"col-md-12\">\n    <p class=\"blue\"><strong>* Ces informations sont requises</strong></p>\n  </div>\n  <div class=\"col-md-12\">\n    \n  </div>\n  <div class=\"col-md-12\">\n    [recaptcha]\n    [submit class:btn class:btn-submit-contact \"Envoyer\"]\n  </div>\n</div>'),
